import React, { useState } from "react";
import Header from "./components/Header";
import TaskList from "./components/TaskList";
import Analytics from "./components/Analytics";
import ThemeSelector from "./components/ThemeSelector";
import Sidebar from "./components/Sidebar";
import { toggleTaskCompletion, setTaskPriority } from "./utils";
import "./styles/App.css";
import "./styles/TaskItem.css";

const App = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: "Welcome to CheckMate! ðŸŽ‰ Start by completing this task to begin your productivity journey.",
      completed: false,
      priority: "medium",
      dueDate: "",
    },
    {
      id: 2,
      title: "Start small: Add just 1 task that's been on your mind ðŸ’†",
      completed: false,
      priority: "low",
      dueDate: "",
    },
    {
      id: 3,
      title: "Do a weekly review of my tasks and goals ðŸ”„",
      completed: false,
      priority: "high",
      dueDate: "",
    },
    {
      id: 4,
      title: "Download CheckMate on your phone for easy access ðŸ“±",
      completed: false,
      priority: "low",
      dueDate: "",
    },
  ]);

  const [filterStatus, setFilterStatus] = useState("all");
  const [theme, setTheme] = useState("light");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedTab, setSelectedTab] = useState("today");
  const [taskInput, setTaskInput] = useState("");  // New state for task input
  const [taskPriority, setTaskPriority] = useState("medium");  // New state for task priority

  const toggleDarkMode = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const addTask = (title, priority) => {
    if (!title.trim()) return; // Prevent empty tasks
  
    const newTask = {
      id: Date.now(),
      title,
      completed: false,
      priority: priority || "medium",
    };
  
    setTasks((prevTasks) => {
      const updatedTasks = [...prevTasks, newTask];
      console.log("Tasks after addition:", updatedTasks); // Log here to confirm the update
      return updatedTasks;
    });
  };  

  const handleToggleTask = (task) => {
    setTasks(tasks.map((t) => (t.id === task.id ? toggleTaskCompletion(t) : t)));
  };

  const handleSetPriority = (task, priority) => {
    setTasks(tasks.map((t) => (t.id === task.id ? setTaskPriority(t, priority) : t)));
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const filteredTasks = tasks.filter((task) =>
    task.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const getTabFilteredTasks = () => {
    switch (selectedTab) {
      case "today":
        return tasks.filter((task) => task.dueDate === "today");
      case "upcoming":
        return tasks.filter((task) => task.dueDate && task.dueDate > "today");
      case "filtered":
        return filteredTasks;
      default:
        return tasks;
    }
  };

  return (
    <div className={theme === "dark" ? "dark-mode" : ""}>
      <div className="app-container">
        <Sidebar onSelectTab={setSelectedTab} />
        <div className="main-content">
          <Header onToggleDarkMode={toggleDarkMode} onSearch={handleSearch} />
          <div className="task-container">
            <TaskList
              tasks={getTabFilteredTasks()}
              filterStatus={filterStatus}
              onToggleTask={handleToggleTask}
              onSetPriority={handleSetPriority}
            />
            <div className="task-input">
              <input
                type="text"
                placeholder="New Task"
                value={taskInput}
                onChange={(e) => setTaskInput(e.target.value)}  // Handle input change
                className="task-input-field"
              />
              <select 
                value={taskPriority} 
                onChange={(e) => setTaskPriority(e.target.value)}  // Handle priority change
                className="priority-selector"
              >
                <option value="high">High</option>
                <option value="medium">Medium</option>
                <option value="low">Low</option>
              </select>
              <button
                onClick={() => {
                  if (taskInput.trim()) {
                    addTask(taskInput, taskPriority);  // Add task with priority
                    setTaskInput("");  // Clear input field after adding task
                  }
                }}
              >
                Add Task
              </button>
            </div>
          </div>
          <Analytics tasks={tasks} />
        </div>
      </div>
    </div>
  );
};

export default App;
