import React from "react";
import ThemeSelector from "./ThemeSelector";

const Header = ({ onToggleDarkMode, theme }) => {
    return (
      <header className={theme === "dark" ? "dark-mode" : ""}>
        <h1>CheckMate: An ADHD-Friendly To-Do List</h1>
        <div className="header-actions">
          <ThemeSelector onThemeChange={onToggleDarkMode} />
          <input type="text" placeholder="Search Tasks" className={`search-bar ${theme === "dark" ? "dark-mode" : ""}`} />
        </div>
      </header>
    );
  };
  
  export default Header;
