import React, { useState } from "react"; // Import useState
import '../styles/TaskItem.css';

const TaskItem = ({ task, onToggleTask, onSetPriority }) => {
    const [showPriority, setShowPriority] = useState(false); // Initialize state to toggle priority visibility

    return (
      <li className="task-item">
        <input 
          type="checkbox" 
          checked={task.completed} 
          onChange={() => onToggleTask(task)} 
        />
        <span className={task.completed ? "completed" : ""}>{task.title}</span>

        {showPriority ? (
          <select 
            value={task.priority} 
            onChange={(e) => onSetPriority(task, e.target.value)}
          >
            <option value="high">High</option>
            <option value="medium">Medium</option>
            <option value="low">Low</option>
          </select>
        ) : (
          <button onClick={() => setShowPriority(true)}>Set Priority</button>
        )}
      </li>
    );
};

export default TaskItem;
