import React from "react";
import { formatDueDate } from "../utils";

const TaskItem = ({ task, onToggleTask, onSetPriority }) => {
  const handleToggleCompletion = () => {
    onToggleTask(task);
  };

  const handlePriorityChange = (e) => {
    onSetPriority(task, e.target.value);
  };

  return (
    <div className={`task-item ${task.completed ? "completed" : ""}`}>
      <input
        type="checkbox"
        checked={task.completed}
        onChange={handleToggleCompletion}
      />
      <div className="task-title">{task.title}</div>
      <div className={`priority ${task.priority}`}>{task.priority}</div>
      {task.dueDate && <div className="due-date">{formatDueDate(task.dueDate)}</div>}
      <select onChange={handlePriorityChange} value={task.priority}>
        <option value="low">Low</option>
        <option value="medium">Medium</option>
        <option value="high">High</option>
      </select>
    </div>
  );
};

export default TaskItem;
