import React from "react";
import '../styles/TaskItem.css';

const TaskItem = ({ task, onToggleTask, onSetPriority }) => {
  return (
    <div className={`task-item ${task.priority} ${task.completed ? "completed" : ""}`}>
      <input
        type="checkbox"
        checked={task.completed}
        onChange={() => onToggleTask(task)}
      />
      <span>{task.title}</span>
      <span className="due-date">{task.dueDate}</span>
      <div className="priority-selector">
        <button onClick={() => onSetPriority(task, "high")}>High</button>
        <button onClick={() => onSetPriority(task, "medium")}>Medium</button>
        <button onClick={() => onSetPriority(task, "low")}>Low</button>
      </div>
    </div>
  );
};

export default TaskItem;
