import React, { useState } from 'react';
import { Container, Form, Button, ListGroup } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('All');  // New state for filtering

  // Function to add a new task
  const handleAddTask = () => {
    if (task !== '') {
      setTasks([...tasks, { task: task, completed: false }]); // Store task and completion status
      setTask('');
    }
  };

  // Function to toggle task completion
  const handleCompleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  // Function to remove a task
  const handleRemoveTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  // Function to filter tasks based on the selected filter
  const filteredTasks = tasks.filter((taskObj) => {
    if (filter === 'All') return true;
    if (filter === 'Completed') return taskObj.completed;
    if (filter === 'Pending') return !taskObj.completed;
    return true;
  });

  return (
    <Container className="mt-4">
      <h1 className="text-center">ADHD-Friendly To-Do List</h1>

      {/* Filter buttons */}
      <div className="text-center mb-3">
        <Button variant="secondary" onClick={() => setFilter('All')}>
          All
        </Button>
        <Button variant="secondary" onClick={() => setFilter('Completed')}>
          Completed
        </Button>
        <Button variant="secondary" onClick={() => setFilter('Pending')}>
          Pending
        </Button>
      </div>

      {/* Input field and Add task button */}
      <Form className="mb-4">
        <Form.Control
          type="text"
          placeholder="Add a task"
          value={task}
          onChange={(e) => setTask(e.target.value)}
        />
        <Button variant="primary" onClick={handleAddTask} className="mt-2 w-100">
          Add Task
        </Button>
      </Form>

      <h2>Your Tasks:</h2>
      <ListGroup>
        {filteredTasks.map((taskObj, index) => (
          <ListGroup.Item
            key={index}
            className="d-flex justify-content-between align-items-center"
            style={{
              textDecoration: taskObj.completed ? 'line-through' : 'none', // Strike-through for completed tasks
            }}
          >
            {/* Checkbox to mark task as completed */}
            <Form.Check
              type="checkbox"
              checked={taskObj.completed}
              onChange={() => handleCompleteTask(index)}
              label={taskObj.task}
            />
            {/* Remove task button */}
            <Button variant="danger" onClick={() => handleRemoveTask(index)}>
              Remove
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
}

export default App;
