import React, { useState } from "react";
import Header from "./components/Header";
import TaskList from "./components/TaskList";
import Analytics from "./components/Analytics";
import ThemeSelector from "./components/ThemeSelector";
import { toggleTaskCompletion, setTaskPriority } from "./utils";
import "./styles/App.css";
import "./styles/TaskItem.css";

const App = () => {
  const [tasks, setTasks] = useState([
    { id: 1, title: "Task 1", completed: false, priority: "medium", dueDate: "2025-01-30" },
    { id: 2, title: "Task 2", completed: true, priority: "high", dueDate: "2025-01-25" },
  ]);
  const [filterStatus, setFilterStatus] = useState("all");
  const [filterPriority, setFilterPriority] = useState("all");
  const [theme, setTheme] = useState("light");
  const [newTaskTitle, setNewTaskTitle] = useState("");

  const toggleDarkMode = () => {
    setTheme((prevTheme) => (prevTheme === "light" ? "dark" : "light"));
  };

  const handleSearch = (searchQuery) => {
    // Implement search functionality
  };

  const handleAddTask = () => {
    if (newTaskTitle.trim()) {
      setTasks([
        ...tasks,
        {
          id: tasks.length + 1,
          title: newTaskTitle,
          completed: false,
          priority: "medium", // Default to medium
          dueDate: "2025-01-30", // Default date, you can modify this later
        },
      ]);
      setNewTaskTitle(""); // Clear the input field after adding
    }
  };

  const handleToggleTask = (task) => {
    setTasks(tasks.map((t) => (t.id === task.id ? toggleTaskCompletion(t) : t)));
  };

  const handleSetPriority = (task, priority) => {
    setTasks(tasks.map((t) => (t.id === task.id ? setTaskPriority(t, priority) : t)));
  };

  const handleFilterTasks = (tasks) => {
    let filteredTasks = tasks;

    if (filterStatus !== "all") {
      filteredTasks = filteredTasks.filter(
        (task) => (filterStatus === "completed" ? task.completed : !task.completed)
      );
    }

    if (filterPriority !== "all") {
      filteredTasks = filteredTasks.filter((task) => task.priority === filterPriority);
    }

    return filteredTasks;
  };

  return (
    <div className={theme === "dark" ? "dark-mode" : ""}>
      <Header onToggleDarkMode={toggleDarkMode} onSearch={handleSearch} />
      <div className="task-input">
        <input
          type="text"
          value={newTaskTitle}
          onChange={(e) => setNewTaskTitle(e.target.value)}
          placeholder="Add a new task"
        />
        <button onClick={handleAddTask}>Add Task</button>
      </div>
      <div className="filters">
        <button onClick={() => setFilterStatus("all")}>All</button>
        <button onClick={() => setFilterStatus("completed")}>Completed</button>
        <button onClick={() => setFilterStatus("pending")}>Pending</button>
        <button onClick={() => setFilterPriority("high")}>High Priority</button>
        <button onClick={() => setFilterPriority("medium")}>Medium Priority</button>
        <button onClick={() => setFilterPriority("low")}>Low Priority</button>
      </div>
      <TaskList
        tasks={handleFilterTasks(tasks)}
        onToggleTask={handleToggleTask}
        onSetPriority={handleSetPriority}
      />
      <Analytics tasks={tasks} />
      <ThemeSelector onThemeChange={setTheme} />
    </div>
  );
};

export default App;
